  @import './assets/styles/variables.scss';

  .footer {
    background-color: $dark-blue;
    flex-direction: row;
    display: flex;
    color: white;
    padding: 6rem 3rem;
    font-family: BerninaSans-Regular;
    justify-content: space-around;
    align-items: center;
    min-height: 30rem;

    .container {
      display: flex;
      flex-direction: column;
      max-width: 100rem;
      width: 100%;

      p {
        margin: 0;
        line-height: 1.5;
        font-size: 1.6rem;
      }
    }

    .logo {
      display: flex;
      justify-content: space-between;
      color: white;
      font-size: 1.4rem;
      border-bottom: 1px solid $blue;
      margin-bottom: 2rem;
      padding-bottom: 3rem;
      align-items: flex-end;
    }

    .link-container {
      width: 100%;

      &:deep div {
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;
        justify-content: space-between;
      }

      &:deep ul {
        list-style: none;
        max-width: 30rem;
        line-height: 2.4;
        padding: 0 3rem;

        li.hed {
          font-size: 1rem;
          text-transform: uppercase;
          line-height: 1.3;
          font-family: BerninaSans-Semibold;
          letter-spacing: 0.4rem;
          margin-bottom: 2rem;
        }

        li {
          font-size: 1.6rem;

          a {
            color: $light-blue;

            &:hover {
              color: white;
            }
          }
        }
      }
    }
}

  @media (max-width: 800px) {
    .container {
      flex-direction: column;
    }
  }

  @media (max-width: 600px) {
    .footer .link-container {
      flex-direction: column;
      justify-content: flex-start;
      padding: 0;

      ul {
        padding: 1rem 0;
      }
    }
  }
