<template>
  <div class="piechart">

    <svg viewBox="0 0 100 100">

      <circle cx="50" cy="50" r="25" fill="transparent"></circle>

      <circle cx="50" cy="50" r="25" fill="transparent" stroke="var(--medium-grey)" stroke-width="10"></circle>

      <circle cx="50" cy="50" r="25" stroke-dasharray="157" :stroke-dashoffset="offset" fill="transparent" stroke="blue" stroke-width="10"></circle>

    </svg>

  </div>
</div>
</template>

<style lang="scss" scoped>
  @import './assets/styles/variables.scss';

  .piechart {
    width: 25rem;
    height: 25rem;

    svg {
      background: none;
    }
  }

</style>

<script>

export default {
  name: 'PieChart',
  props: ['percent'],
  computed: {
    offset: function (){
      return 157 - Math.ceil(this.percent*157)
    }
  }
}

</script>
