 <template>
  <ul class="breadcrumbs">
    <li><a href="/">Home</a></li>
    <li v-for="crumb in crumbs"><a :href="getPath(crumb)">{{crumb}}</a></li>
  </ul>
</template>

<script>
export default {
  name: "Breadcrumbs",
  computed: {
    crumbs: function() {
      let pathArray = this.$route.path.split("/")
      pathArray.shift()
      return pathArray;
    }
  },
  methods: {
    getPath(crumb) {
      var path = "/";
      for (let i=0; i < this.crumbs.length; i++) {
        path += this.crumbs[i] + '/';
        if(this.crumbs[i] == crumb) break;
      }
      return path;
    }
  }
};
</script>

<style lang="scss" scoped>

  .breadcrumbs {
    list-style: none;
    display: flex;

    li {
      padding: 0 1rem;  
    }

  }

</style>